package com.victor.mvp.presenter.impl;import com.victor.mvp.model.WeatherModel;import com.victor.mvp.model.data.Weather;import com.victor.mvp.model.impl.WeatherModelImpl;import com.victor.mvp.presenter.OnWeatherListener;import com.victor.mvp.presenter.WeatherPresenter;import com.victor.mvp.ui.view.WeatherView;/** * Created by victor on 2017/2/8. * 天气 Prestener实现 */public class WeatherPresenterImpl implements WeatherPresenter, OnWeatherListener {	/*Presenter作为中间层，持有View和Model的引用*/	private WeatherView weatherView;	private WeatherModel weatherModel;	public WeatherPresenterImpl(WeatherView weatherView) {		this.weatherView = weatherView;		weatherModel = new WeatherModelImpl();	}	@Override	public void getWeather(String cityNO) {		weatherView.showLoading();		weatherModel.loadWeather(cityNO, this);	}	@Override	public void onSuccess(Weather weather) {		weatherView.hideLoading();		weatherView.setWeatherInfo(weather);	}	@Override	public void onError() {		weatherView.hideLoading();		weatherView.showError();	}}